import React, { useRef } from "react";

import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import { KeyboardArrowRight } from "@mui/icons-material";
import { makeStyles } from "@mui/styles";
import TextField from "@mui/material/TextField";

const useStyles = makeStyles({
  field: {
    marginTop: 20,
    marginBottom: 20,
    display: "block",
  },
});

export default function Create() {
  const noteFieldRef = useRef();
  const descFieldRef = useRef();

  const submitHandler = (e) => {
    e.preventDefault();

    const enteredNote = noteFieldRef.current.value;
    const enteredDesc = descFieldRef.current.value;

    console.log(noteFieldRef.current.value, enteredDesc);
  };

  const classes = useStyles();

  return (
    <div>
      <Container>
        <Typography
          variant="h6"
          component="h2"
          gutterBottom
          color="textSecondary"
        >
          Create a new note
        </Typography>

        <form noValidate autoComplete="off" onSubmit={submitHandler}>
          <TextField
            className={classes.field}
            label="Note Title"
            variant="outlined"
            color="secondary"
            fullWidth
            required
            ref={noteFieldRef}
          />

          <TextField
            className={classes.field}
            label="Note Description"
            variant="outlined"
            color="secondary"
            multiline
            rows={4}
            fullWidth
            required
            ref={descFieldRef}
          />

          <Button
            type="submit"
            color="secondary"
            variant="contained"
            endIcon={<KeyboardArrowRight />}
          >
            Submit
          </Button>
        </form>
      </Container>
    </div>
  );
}
